import React, { Component } from 'react'
import styled, { createGlobalStyle } from "styled-components"

class Login extends Component {
    constructor(props) {
        super()
        this.state = {
            usr: '',
            pwd: '',
            forgotText: 'Forgot password?',
        }
    }
    h_enter = e => {
        //check if enter key was pressed
        if(e.key === 'Enter') {
            this.h_checkStreetCred()
        }
    }
    h_updateUsername = e => {
        this.setState({usr: e.target.value})
    }
    h_updatePassword = e => {
        this.setState({pwd: e.target.value})
    }
    h_checkStreetCred = () => {
        //don't bother checking for cred, just let them in
        const usr = this.state.usr
        const pwd = this.state.pwd
        localStorage.setItem('username', JSON.stringify(usr))
        localStorage.setItem('password', JSON.stringify(pwd))
        window.location.reload()
    }
    h_forgotPassword = () => {
        this.setState({forgotText: 'That sucks.'})
    }
    render() {
        return (
            <React.Fragment>
                <GlobalStyle />
                <LOGIN>
                    <h1>Login</h1>
                </LOGIN>
            </React.Fragment>
        )
    }
}

export default Login

const GlobalStyle = createGlobalStyle`
  #root {
    background-color: #000;
    height: 100vh;
    width: 100vw;
    color: #fff;
  }
`
const LOGIN = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    // background-color: #fff;
`
