import React from 'react'
import styled from 'styled-components'

const Center = ({player}) => {
    return (
        <CENTER>
            <div className='row'>
                <div className='col'>
                    <pre>You currently live in</pre>
                    <span className='bold'>Las Vegas</span>
                </div>
            </div>
            <div className='row'>
                <div className='col'>
                    <span className='big bold underline'>Money:</span>
                    <span className='huge'>${player.cash}</span>
                </div>
                <div className='col'>
                    <span className='big bold underline'>Turns:</span>
                    <span className='huge'>300</span>
                </div>
            </div>
            <div className='row'>
                <pre>You have been a member of the alliance</pre>
                <span>{player.alliance}</span>
                <pre>for two weeks.</pre>
            </div>
        </CENTER>
    )
}

export default Center

const CENTER = styled.div`
    display: flex;
    flex-direction: column;
    font-family: verdana;
    font-size: 1.3rem;
    text-align: center;

    .bold {font-weight: 700}
    .big {font-size: 1.6rem}
    .huge {font-size: 1.92rem}
    .underline {text-decoration: underline}

    .row {
        margin: 0.5rem 0;
        background-color: #222;
        display: inline-block;

        .col {
            display: flex;
            flex-direction: column;
            background-color: #444;
            width: fit-content;
        }
    }
`
