import React from 'react'
import styled from 'styled-components'

const Center = ({player}) => {
    return (
        <CENTER>
            <div className='row'>
                <div className='col'>
                    <pre>You currently live in</pre>
                    <span className='bold'>Las Vegas</span>
                </div>
            </div>
            <div className='row'>
                <div className='col'>
                    <span className='big underline'>Money:</span>
                    <span className='huge'>${player.cash}</span>
                </div>
                <div className='col'>
                    <span className='big underline'>Turns:</span>
                    <span className='huge'>300</span>
                </div>
            </div>
            <div className='row'>
                <pre>You have been a member of the alliance</pre>
                <span className='link'>{player.alliance}</span>
                <pre>for two weeks.</pre>
            </div>
            <div className='row'>
                <div className='col'>
                    <span className='underline'>Whore payout:</span>
                    <div className='row'>
                        <pre>%</pre>
                        <input type='number' value={player.payout}/>
                        <button>SET</button>
                    </div>
                    <span>Whore Happiness: {player.whoreHappiness}</span>
                    <span>Thug Happiness: {player.thugHappiness}</span>
                </div>
            </div>
            <div className='row'>
                <pre>Attacks:</pre>
                <pre>In:</pre>
                <span className='red'>{player.attacksIn}</span>
                <pre>Out:</pre>
                <span className='red'>{player.attacksOut}</span>
            </div>
        </CENTER>
    )
}

export default Center

const CENTER = styled.div`
    display: flex;
    flex-direction: column;
    font-family: verdana;
    font-size: 1.3rem;
    text-align: center;

    .big {font-size: 1.6rem}
    .bold {font-weight: 700}
    .huge {font-size: 1.92rem}
    .link { color: #09f}
    .red {color: #f00}
    .underline {text-decoration: underline}

    input {
        margin: 0 0.5rem;
        width: 40px;
    }
    button {
        border: none;
        background-color: transparent;
        color: #fff;
        font-weight: 700;
    }
    .row {
        background-color: #222;
        display: flex;
        justify-content: center;
        margin: 0.5rem 0;
    }
    .col {
        background-color: #444;
        display: flex;
        flex-direction: column;
        margin: 0 0.5rem;
        width: fit-content;
    }
`
